export class MeetingListComponent {
  constructor(meetingService, clientService) {
    this.meetingService = meetingService;
    this.clientService = clientService;
    this.meetings = [];
    this.filteredMeetings = [];
    this.clients = [];
    this.searchTerm = '';
    this.statusFilter = '';
    this.typeFilter = '';
    this.showAddForm = false;
    this.newMeetingDate = '';
    this.newMeeting = {
      clientId: 0,
      clientName: '',
      title: '',
      description: '',
      time: '10:00',
      duration: 60,
      location: 'Office',
      type: 'consultation',
      status: 'scheduled'
    };
  }
  ngOnInit() {
    this.loadMeetings();
    this.loadClients();
  }
  loadMeetings() {
    this.meetingService.getMeetings().subscribe(meetings => {
      this.meetings = meetings.sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
      this.filteredMeetings = this.meetings;
    });
  }
  loadClients() {
    this.clientService.getClients().subscribe(clients => {
      this.clients = clients;
    });
  }
  filterMeetings() {
    let filtered = this.meetings;
    if (this.searchTerm) {
      const search = this.searchTerm.toLowerCase();
      filtered = filtered.filter(meeting => meeting.title.toLowerCase().includes(search) || meeting.clientName.toLowerCase().includes(search) || meeting.description.toLowerCase().includes(search) || meeting.location.toLowerCase().includes(search));
    }
    if (this.statusFilter) {
      filtered = filtered.filter(meeting => meeting.status === this.statusFilter);
    }
    if (this.typeFilter) {
      filtered = filtered.filter(meeting => meeting.type === this.typeFilter);
    }
    this.filteredMeetings = filtered;
  }
  onClientChange() {
    const selectedClient = this.clients.find(c => c.id === Number(this.newMeeting.clientId));
    if (selectedClient) {
      this.newMeeting.clientName = selectedClient.name;
    }
  }
  addMeeting() {
    if (this.newMeeting.title && this.newMeeting.clientId && this.newMeetingDate) {
      const meetingToAdd = {
        ...this.newMeeting,
        date: new Date(this.newMeetingDate),
        createdDate: new Date()
      };
      this.meetingService.addMeeting(meetingToAdd);
      this.resetForm();
      this.showAddForm = false;
      this.loadMeetings();
    }
  }
  resetForm() {
    this.newMeeting = {
      clientId: 0,
      clientName: '',
      title: '',
      description: '',
      time: '10:00',
      duration: 60,
      location: 'Office',
      type: 'consultation',
      status: 'scheduled'
    };
    this.newMeetingDate = '';
  }
  editMeeting(meeting) {
    // For now, just log - in a real app, this would open an edit modal
    console.log('Edit meeting:', meeting);
  }
  markCompleted(meeting) {
    const updatedMeeting = {
      ...meeting,
      status: 'completed'
    };
    this.meetingService.updateMeeting(updatedMeeting);
    this.loadMeetings();
  }
  rescheduleMeeting(meeting) {
    const updatedMeeting = {
      ...meeting,
      status: 'rescheduled'
    };
    this.meetingService.updateMeeting(updatedMeeting);
    this.loadMeetings();
  }
  deleteMeeting(id) {
    if (confirm('Are you sure you want to delete this meeting?')) {
      this.meetingService.deleteMeeting(id);
      this.loadMeetings();
    }
  }
  isPastMeeting(meeting) {
    const today = new Date();
    const meetingDate = new Date(meeting.date);
    return meetingDate < today && meeting.status !== 'completed';
  }
