export class ClientListComponent implements OnInit {
  clients: Client[] = [];
  filteredClients: Client[] = [];
  searchTerm = '';
  statusFilter = '';
  showAddForm = false;

  newClient: Partial<Client> = {
    name: '',
    email: '',
    phone: '',
    company: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    projectType: 'Residential',
    status: 'prospect',
    notes: ''
  };

  constructor(private clientService: ClientService) {}

  ngOnInit(): void {
    this.loadClients();
  }

  loadClients(): void {
    this.clientService.getClients().subscribe(clients => {
      this.clients = clients;
      this.filteredClients = clients;
    });
  }

  filterClients(): void {
    let filtered = this.clients;

    if (this.searchTerm) {
      const search = this.searchTerm.toLowerCase();
      filtered = filtered.filter(client =>
        client.name.toLowerCase().includes(search) ||
        client.company.toLowerCase().includes(search) ||
        client.email.toLowerCase().includes(search) ||
        client.projectType.toLowerCase().includes(search)
      );
    }

    if (this.statusFilter) {
      filtered = filtered.filter(client => client.status === this.statusFilter);
    }

    this.filteredClients = filtered;
  }

  addClient(): void {
    if (this.newClient.name && this.newClient.email) {
      const clientToAdd = {
        ...this.newClient,
        createdDate: new Date(),
        lastContact: new Date()
      } as Omit<Client, 'id'>;

      this.clientService.addClient(clientToAdd);
      this.resetForm();
      this.showAddForm = false;
      this.loadClients();
    }
  }

  resetForm(): void {
    this.newClient = {
      name: '',
      email: '',
      phone: '',
      company: '',
      address: '',
      city: '',
      state: '',
      zipCode: '',
      projectType: 'Residential',
      status: 'prospect',
      notes: ''
    };
  }

  editClient(client: Client): void {
    // For now, just log - in a real app, this would open an edit modal
    console.log('Edit client:', client);
  }

  deleteClient(id: number): void {
    if (confirm('Are you sure you want to delete this client?')) {
      this.clientService.deleteClient(id);
      this.loadClients();
    }
  }
}
