import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { Client } from '../models/client.model';

@Injectable({
  providedIn: 'root'
})
export class ClientService {
  private clients: Client[] = [
    {
      id: 1,
      name: 'John Smith',
      email: 'john.smith@email.com',
      phone: '+1-555-0101',
      company: 'Smith Enterprises',
      address: '123 Main St',
      city: 'Austin',
      state: 'TX',
      zipCode: '78701',
      projectType: 'Residential',
      status: 'active',
      createdDate: new Date('2024-01-15'),
      lastContact: new Date('2024-10-20'),
      notes: 'Looking for modern home design with sustainable features'
    },
    {
      id: 2,
      name: 'Sarah Johnson',
      email: 'sarah.j@johnsoncorp.com',
      phone: '+1-555-0102',
      company: 'Johnson Corp',
      address: '456 Oak Ave',
      city: 'Austin',
      state: 'TX',
      zipCode: '78702',
      projectType: 'Commercial',
      status: 'active',
      createdDate: new Date('2024-02-10'),
      lastContact: new Date('2024-10-25'),
      notes: 'Office building renovation project - 50,000 sq ft'
    },
    {
      id: 3,
      name: 'Mike Davis',
      email: 'mike.davis@davisholdings.com',
      phone: '+1-555-0103',
      company: 'Davis Holdings',
      address: '789 Pine St',
      city: 'Austin',
      state: 'TX',
      zipCode: '78703',
      projectType: 'Mixed Use',
      status: 'prospect',
      createdDate: new Date('2024-10-01'),
      lastContact: new Date('2024-10-15'),
      notes: 'Interested in mixed-use development downtown'
    },
    {
      id: 4,
      name: 'Lisa Chen',
      email: 'lisa.chen@techstart.com',
      phone: '+1-555-0104',
      company: 'TechStart Inc',
      address: '321 Cedar Ln',
      city: 'Austin',
      state: 'TX',
      zipCode: '78704',
      projectType: 'Commercial',
      status: 'active',
      createdDate: new Date('2024-03-20'),
      lastContact: new Date('2024-10-28'),
      notes: 'Tech campus design - modern and collaborative spaces'
    }
  ];

  private clientsSubject = new BehaviorSubject<Client[]>(this.clients);

  getClients(): Observable<Client[]> {
    return this.clientsSubject.asObservable();
  }

  getClientById(id: number): Client | undefined {
    return this.clients.find(client => client.id === id);
  }

  addClient(client: Omit<Client, 'id'>): void {
    const newClient: Client = {
      ...client,
      id: Math.max(...this.clients.map(c => c.id)) + 1
    };
    this.clients.push(newClient);
    this.clientsSubject.next([...this.clients]);
  }

  updateClient(updatedClient: Client): void {
    const index = this.clients.findIndex(c => c.id === updatedClient.id);
    if (index !== -1) {
      this.clients[index] = updatedClient;
      this.clientsSubject.next([...this.clients]);
    }
  }

  deleteClient(id: number): void {
    this.clients = this.clients.filter(c => c.id !== id);
    this.clientsSubject.next([...this.clients]);
  }

  searchClients(query: string): Client[] {
    const lowercaseQuery = query.toLowerCase();
    return this.clients.filter(client =>
      client.name.toLowerCase().includes(lowercaseQuery) ||
      client.company.toLowerCase().includes(lowercaseQuery) ||
      client.email.toLowerCase().includes(lowercaseQuery) ||
      client.projectType.toLowerCase().includes(lowercaseQuery)
    );
  }

  getClientsByStatus(status: Client['status']): Client[] {
    return this.clients.filter(client => client.status === status);
  }
}
